mapper.select:
/* AcutMgmtDao.getUserGust - javayaji */
SELECT UG.GUST_SEQ
     , UG.USER_NO
     , UG.CLNT_IP
     , DATE_FORMAT(UG.LAST_LGIN_DATE, '%Y/%m/%d %H:%i') LAST_LGIN_DATE
     , DATE_FORMAT(UG.RGST_DATE, '%Y/%m/%d %H:%i') RGST_DATE
     , UG.RGST_USER_NO
  FROM JV_USER_GUST UG
 WHERE UG.DB_STAT = 'A'

        AND GUST_SEQ = ${gustSeq}


        AND USER_NO = ${userNo}


        AND CLNT_IP = #{clntIp}

/* AcutMgmtDao.getUser - javayaji */ SELECT U.USER_NO as userNo , U.LGIN_ID , U.IDVL_EMAL , U.GUST_YN , U.GNDR , DATE_FORMAT(U.PSWD_CHNG_DATE, '%Y/%m/%d %H:%i') PSWD_CHNG_DATE , DATE_FORMAT(U.MDFT_DATE, '%Y/%m/%d %H:%i') MDFT_DATE , U.MDFT_USER_NO , DATE_FORMAT(U.RGST_DATE, '%Y/%m/%d %H:%i') RGST_DATE , U.RGST_USER_NO FROM JV_USER U WHERE U.DB_STAT = 'A'
AND U.USER_NO = ${userNo}
AND U.LGIN_ID = #{lginId}

mapper.insert:
/* AcutMgmtDao.insUser - javayaji */ INSERT INTO JV_USER ( USER_NKNM , LGIN_ID , WEB_PSWD , IDVL_EMAL , GUST_YN , GNDR , DB_STAT , PSWD_CHNG_DATE , MDFT_DATE , MDFT_USER_NO , RGST_DATE , RGST_USER_NO ) VALUES ( #{lginId} , #{lginId}
, '1' , NULL , 'Y' , NULL , 'A' , NOW() , NOW() , 1 , NOW() , 1
, JV_ENC_F(#{webPswd}) , JV_ENC_F(#{idvlEmal}) , 'N' , #{gndr} , 'A' , NOW() , NOW() , 1 , NOW() , 1
)
