mapper.select:
/* CommDao.selCode - javayaji */ SELECT CODE_INI , CODE_INI_NAME , CODE_ID , CODE_ID_NAME , PRNT_INI , PRNT_ID , USE_YN , DISP_YN , DISP_ODER , CODE_DESC , DATE_FORMAT(RGST_DATE, '%Y/%m/%d %H:%i') RGST_DATE , RGST_USER_NO FROM JV_CODE
AND CODE_GRUP = #{codeGrup}
AND CODE_ID = #{codeId}
AND USE_YN = 'Y'
AND DISP_YN = 'Y'
AND PRNT_GRUP = #{prntGrup}
AND PRNT_ID = #{prntId}
ORDER BY DISP_ODER

mapper.insert:
/* CommDao.insCode - javayaji */
INSERT INTO JV_CODE (
CODE_INI
, CODE_INI_NAME
, CODE_ID
, CODE_ID_NAME
, PRNT_INI
, PRNT_ID
, USE_YN
, DISP_YN
, DISP_ODER
, CODE_DESC
, RGST_DATE
, RGST_USER_NO
)
VALUES

    (
    #{item.codeIni}
    , #{item.codeIniName}
    , #{item.codeId}
    , #{item.codeIdName}
    , #{item.prntIni}
    , #{item.prntId}
    , IFNULL(#{item.useYn}, 'Y')
    , IFNULL(#{item.dispYn}, 'Y')
    , #{index}
    , #{item.codeDesc}
    , NOW()
    , #{item.rgstUserNo}
    )

ON DUPLICATE KEY UPDATE
CODE_INI_NAME = VALUES(CODE_INI_NAME)
, CODE_ID_NAME = VALUES(CODE_ID_NAME)
, PRNT_INI = VALUES(PRNT_INI)
, PRNT_ID = VALUES(PRNT_ID)
, USE_YN = VALUES(USE_YN)
, DISP_YN = VALUES(DISP_YN)
, DISP_ODER = VALUES(DISP_ODER)
, RGST_DATE = VALUES(RGST_DATE)
, RGST_USER_NO = VALUES(RGST_USER_NO)
